function validateForm(){return document.getElementById("email").value.length>0?(document.getElementById("submit_btn").disabled=!1,!0):(document.getElementById("submit_btn").disabled=!0,!1)}function sendPassResetEmail(msg){var e=firebase.auth(),t=document.getElementById("email").value;return e.sendPasswordResetEmail(t).then(function(){displayAlert(msg)}).catch(function(e){displayAlert(e.message)}),!1}